
public class Aritmatika {

	/**
	 * @param args
	 * Penggunaan operator aritmatika
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int nilai1 = 30;
		int nilai2 = 7;
		
		int penjumlahan = nilai1 + nilai2;
		int pengurangan = nilai1 + nilai2;
		float pembagian = (float)nilai1 / nilai2;
		int perkalian = nilai1 * nilai2;
		float modulus = nilai1 % nilai2;
		
		System.out.println("Hasil penjumlahan = " + penjumlahan);
		System.out.println("Hasil pengurangan = " + pengurangan);
		System.out.println("Hasil pembagian = " + pembagian);
		System.out.println("Hasil perkalian = " + perkalian);
		System.out.println("Hasil modulus = " + modulus);		
	}
}
